var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate){
    // Create GlideDateTime objects for the current date and the passed in date
    var rightnow = new GlideDateTime();
    var testdate = new GlideDateTime(strDate);

    // If the testdate is before rightnow, return true, else return false
    if (testdate.before(rightnow)) {
      return true;
    }
    else {

	rightnow = rightnow.getLocalDate(); //set right now to local date
	whenNeeded = whenNeeded.getLocalDate(); //set to local date

	if((rightnow.compareTo(whenNeeded))==0)
		{
		gs.addErrorMessage("When needed date cannot submited on the same day. Your request has not been saved to the database.");
		return true;
		}

      return false;
    }
  },

    type: 'NeedItUtils'
};
